// src/api.js

const BASE_URL = 'http://localhost:8000/movies';

export const fetchMovieTitles = async () => {
    const response = await fetch(`${BASE_URL}/titles/`);
    if (!response.ok) {
        throw new Error('Error fetching movie titles');
    }
    const data = await response.json();
    return data;
};

export const fetchRecommendations = async (title, genre = '', minRating = 0) => {
    const response = await fetch(`${BASE_URL}/recommend/${encodeURIComponent(title)}/genre/${encodeURIComponent(genre)}/rating/${minRating}/`);
    if (!response.ok) {
        throw new Error('Error fetching movie recommendations');
    }
    const data = await response.json();
    return data;
};
