import React, { useState, useEffect } from 'react';
import { fetchRecommendations, fetchMovieTitles } from '../api';
import MovieCarousel from './MovieCarousel';
import './MovieSearch.css';

const movieIds = [
    550, 1366, 299536, 297762, 19995, 4922, 142061, 578, 597, 1726, 102382, 503314, 103
];

const MovieSearch = () => {
    const [inputValue, setInputValue] = useState('');
    const [titles, setTitles] = useState([]);
    const [filteredTitles, setFilteredTitles] = useState([]);
    const [recommendations, setRecommendations] = useState([]);
    const [error, setError] = useState('');
    const [showSuggestions, setShowSuggestions] = useState(false);
    const [carouselMovies, setCarouselMovies] = useState([]);

    // Fetch movie details for carousel
    useEffect(() => {
        const loadCarouselMovies = async () => {
            try {
                const moviePromises = movieIds.map(id =>
                    fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=YOUR_API_KEY`)
                        .then(response => response.json())
                );
                const movies = await Promise.all(moviePromises);
                setCarouselMovies(movies);
            } catch (error) {
                console.error('Error fetching movie details for carousel:', error);
                setError('Error fetching movie details for carousel.');
            }
        };

        loadCarouselMovies();
    }, []);

    // Fetch movie titles from backend
    useEffect(() => {
        const loadTitles = async () => {
            try {
                const titles = await fetchMovieTitles();
                setTitles(titles);
            } catch (error) {
                setError('Error fetching movie titles.');
            }
        };

        loadTitles();
    }, []);

    // Filter titles based on input value
    useEffect(() => {
        if (inputValue) {
            setFilteredTitles(titles.filter(title =>
                title.toLowerCase().includes(inputValue.toLowerCase())
            ));
            setShowSuggestions(true);
        } else {
            setFilteredTitles([]);
            setShowSuggestions(false);
        }
    }, [inputValue, titles]);

    // Handle input change
    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    // Handle suggestion click
    const handleSuggestionClick = (title) => {
        setInputValue(title);
        setShowSuggestions(false);
    };

    // Perform search based on the current input value
    const handleSearch = async () => {
        try {
            const data = await fetchRecommendations(inputValue);
            setRecommendations(data);
            setError('');
        } catch (error) {
            setError('Error fetching recommendations.');
        }
    };

    return (
        <div className="MovieSearch">
            <h1>Movie Recommender</h1>
            {/* Display the carousel with movie posters */}
            <MovieCarousel movies={carouselMovies} />
            <div style={{ position: 'relative', marginBottom: '20px' }}>
                <input
                    type="text"
                    value={inputValue}
                    onChange={handleInputChange}
                    placeholder="Type to filter movies"
                    className="search-input"
                />
                {showSuggestions && (
                    <ul className="suggestions-list">
                        {filteredTitles.map((title, index) => (
                            <li
                                key={index}
                                onClick={() => handleSuggestionClick(title)}
                                className="suggestion-item"
                            >
                                {title}
                            </li>
                        ))}
                    </ul>
                )}
            </div>
            <button onClick={handleSearch} className="search-button">Search</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div className="movie-recommendations">
                {recommendations.length > 0 && (
                    <div className="recommendations-scroll">
                        {recommendations.map((movie) => (
                            <div key={movie.id} className="recommendation-item">
                                <h2>{movie.title}</h2>
                                <img
                                    src={movie.poster_path ? `https://image.tmdb.org/t/p/w500/${movie.poster_path}` : 'https://via.placeholder.com/500x750?text=No+Image'}
                                    alt={movie.title}
                                    onError={(e) => {
                                        e.target.onerror = null;
                                        e.target.src = 'https://via.placeholder.com/500x750?text=No+Image';
                                    }}
                                />
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default MovieSearch;
