import React, { useState, useEffect } from 'react';
import MovieCarousel from './MovieCarousel';
import './MovieSearch.css';

// Static list of movie IDs for the carousel
const movieIds = [
    550, 1366, 299536, 297762, 19995, 4922, 142061, 578, 597, 1726, 102382, 503314, 103
];

const fetchPoster = async (movieId) => {
    const apiKey = 'db500e03d11e130a088f324c0da44299';
    const url = `https://api.themoviedb.org/3/movie/${movieId}?api_key=${apiKey}`;
    const response = await fetch(url);
    const data = await response.json();
    return data.poster_path ? `https://image.tmdb.org/t/p/w500/${data.poster_path}` : '';
};

const MovieSearch = () => {
    const [inputValue, setInputValue] = useState('');
    const [titles, setTitles] = useState([]);
    const [filteredTitles, setFilteredTitles] = useState([]);
    const [recommendations, setRecommendations] = useState([]);
    const [error, setError] = useState('');
    const [showSuggestions, setShowSuggestions] = useState(false);
    const [carouselMovies, setCarouselMovies] = useState([]);

    // Fetch movie titles from backend
    useEffect(() => {
        const loadTitles = async () => {
            try {
                const response = await fetch('/api/movie-titles');
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                const contentType = response.headers.get('content-type');
                if (contentType && contentType.includes('application/json')) {
                    const data = await response.json();
                    setTitles(data);
                } else {
                    const text = await response.text(); // Read response text for debugging
                    throw new Error(`Expected JSON but received: ${text}`);
                }
            } catch (error) {
                console.error(`Error fetching movie titles: ${error}`);
                setError('Error fetching movie titles.');
            }
        };

        loadTitles();
    }, []);

    // Fetch static movie posters for the carousel
    useEffect(() => {
        const loadCarouselMovies = async () => {
            try {
                const posters = await Promise.all(movieIds.map(id => fetchPoster(id)));
                setCarouselMovies(posters);
            } catch (error) {
                console.error(`Error fetching carousel movies: ${error}`);
                setError('Error fetching carousel movies.');
            }
        };

        loadCarouselMovies();
    }, []);

    // Filter titles based on input value
    useEffect(() => {
        if (inputValue) {
            setFilteredTitles(titles.filter(title =>
                title.toLowerCase().includes(inputValue.toLowerCase())
            ));
            setShowSuggestions(true);
        } else {
            setFilteredTitles([]);
            setShowSuggestions(false);
        }
    }, [inputValue, titles]);

    // Handle input change
    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    // Handle suggestion click
    const handleSuggestionClick = (title) => {
        setInputValue(title);
        setShowSuggestions(false);
    };

    const handleSearch = async () => {
        try {
            const response = await fetch(`/api/recommend/${encodeURIComponent(inputValue)}`);
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const contentType = response.headers.get('content-type');
            if (contentType && contentType.includes('application/json')) {
                const data = await response.json();
                setRecommendations(data);
                setError('');
            } else {
                const text = await response.text(); // Read response text for debugging
                throw new Error(`Received non-JSON response: ${text}`);
            }
        } catch (error) {
            console.error(`Error fetching recommendations: ${error}`);
            setError('Error fetching recommendations.');
        }
    };

    return (
        <div className="MovieSearch">
            <h1>Movie Recommender</h1>
            <MovieCarousel movies={carouselMovies} />
            <div style={{ position: 'relative', marginBottom: '20px' }}>
                <input
                    type="text"
                    value={inputValue}
                    onChange={handleInputChange}
                    placeholder="Type to filter movies"
                    className="search-input"
                />
                {showSuggestions && (
                    <ul className="suggestions-list">
                        {filteredTitles.map((title, index) => (
                            <li
                                key={index}
                                onClick={() => handleSuggestionClick(title)}
                                className="suggestion-item"
                            >
                                {title}
                            </li>
                        ))}
                    </ul>
                )}
            </div>
            <button onClick={handleSearch} className="search-button">Search</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div className="movie-recommendations">
                {recommendations.length > 0 && (
                    <div className="recommendations-scroll">
                        {recommendations.map((movie) => (
                            <div key={movie.id} className="recommendation-item">
                                <h2>{movie.title}</h2>
                                <img
                                    src={movie.poster_path ? `https://image.tmdb.org/t/p/w500/${movie.poster_path}` : 'https://via.placeholder.com/500x750?text=No+Image'}
                                    alt={movie.title}
                                    onError={(e) => {
                                        e.target.onerror = null;
                                        e.target.src = 'https://via.placeholder.com/500x750?text=No+Image';
                                    }}
                                />
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default MovieSearch;
