const BASE_URL = 'http://localhost:8000'; // Update with your Django server URL

export const fetchMovieTitles = async () => {
    try {
        const response = await fetch(`${BASE_URL}/movies/titles/`);
        if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return await response.json();
    } catch (error) {
        console.error('Error fetching movie titles:', error);
        throw error;
    }
};

export const fetchRecommendations = async (movieTitle) => {
    try {
        const response = await fetch(`${BASE_URL}/movies/recommend/${encodeURIComponent(movieTitle)}/`);
        if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return await response.json();
    } catch (error) {
        console.error('Error fetching movie recommendations:', error);
        throw error;
    }
};
