import React, { useState, useEffect } from 'react';
import { fetchRecommendations, fetchMovieTitles, fetchMoviePosters } from '../api';
import MovieCarousel from './MovieCarousel'; // Import the carousel component

const MovieSearch = () => {
    const [inputValue, setInputValue] = useState('');
    const [titles, setTitles] = useState([]);
    const [filteredTitles, setFilteredTitles] = useState([]);
    const [recommendations, setRecommendations] = useState([]);
    const [error, setError] = useState('');
    const [showSuggestions, setShowSuggestions] = useState(false);
    const [imageUrls, setImageUrls] = useState([]); // State for movie posters

    // Fetch movie titles from backend
    useEffect(() => {
        const loadTitles = async () => {
            try {
                const titles = await fetchMovieTitles();
                setTitles(titles);
            } catch (error) {
                setError('Error fetching movie titles.');
            }
        };

        loadTitles();
    }, []);

    // Fetch movie posters for the carousel
    useEffect(() => {
        const loadPosters = async () => {
            try {
                const posters = await fetchMoviePosters();
                setImageUrls(posters);
            } catch (error) {
                console.error('Error fetching movie posters:', error);
            }
        };

        loadPosters();
    }, []);

    // Filter titles based on input value
    useEffect(() => {
        if (inputValue) {
            setFilteredTitles(titles.filter(title =>
                title.toLowerCase().includes(inputValue.toLowerCase())
            ));
            setShowSuggestions(true);
        } else {
            setFilteredTitles([]);
            setShowSuggestions(false);
        }
    }, [inputValue, titles]);

    // Handle input change
    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    // Handle suggestion click
    const handleSuggestionClick = (title) => {
        setInputValue(title);
        setShowSuggestions(false);
    };

    // Perform search based on the current input value
    const handleSearch = async () => {
        try {
            const data = await fetchRecommendations(inputValue);
            setRecommendations(data);
            setError('');
        } catch (error) {
            setError('Error fetching recommendations.');
        }
    };

    return (
        <div>
            <h1>Movie Recommender</h1>
            <div style={{ marginBottom: '20px' }}>
                <input
                    type="text"
                    value={inputValue}
                    onChange={handleInputChange}
                    placeholder="Type to filter movies"
                    style={{ width: '100%', padding: '8px', fontSize: '16px' }}
                />
                {showSuggestions && (
                    <ul style={{
                        backgroundColor: 'white',
                        border: '1px solid #ccc',
                        listStyleType: 'none',
                        padding: 0,
                        margin: 0,
                        width: '100%',
                        maxHeight: '200px',
                        overflowY: 'auto',
                        zIndex: 1000
                    }}>
                        {filteredTitles.map((title, index) => (
                            <li
                                key={index}
                                onClick={() => handleSuggestionClick(title)}
                                style={{
                                    padding: '8px',
                                    cursor: 'pointer',
                                    borderBottom: '1px solid #ddd'
                                }}
                            >
                                {title}
                            </li>
                        ))}
                    </ul>
                )}
            </div>
            <button onClick={handleSearch} style={{ padding: '10px 20px', fontSize: '16px' }}>Search</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}

            {/* Add the Movie Carousel here */}
            <div style={{ marginTop: '20px' }}>
                {imageUrls.length > 0 && <MovieCarousel imageUrls={imageUrls} />}
            </div>

            <div style={{ marginTop: '20px' }}>
                {recommendations.length > 0 && recommendations.map((movie) => (
                    <div key={movie.id} style={{ marginBottom: '20px' }}>
                        <h2>{movie.title}</h2>
                        <img
                            src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                            alt={movie.title}
                            style={{ width: '150px', height: 'auto' }}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MovieSearch;
