// src/api.js

const BASE_URL = 'http://localhost:8000/movies';

export const fetchMovieTitles = async () => {
    const response = await fetch(`${BASE_URL}/titles/`);
    if (!response.ok) {
        throw new Error('Error fetching movie titles');
    }
    const data = await response.json();
    return data;
};

export const fetchRecommendations = async (title) => {
    const response = await fetch(`${BASE_URL}/recommend/${encodeURIComponent(title)}/`);
    if (!response.ok) {
        throw new Error('Error fetching movie recommendations');
    }
    const data = await response.json();
    return data;
};

export const fetchMoviePosters = async () => {
    try {
        const response = await fetch('/api/movie-posters'); // Replace with your API endpoint
        if (!response.ok) {
            throw new Error('Network response was not ok');
        }
        const data = await response.json();
        return data; // Ensure this is an array of image URLs
    } catch (error) {
        console.error('Error fetching movie posters:', error);
        throw error;
    }
};
