import React, { useState, useEffect } from 'react';
import { fetchRecommendations, fetchMovieTitles } from '../api';

const MovieSearch = () => {
    const [inputValue, setInputValue] = useState('');
    const [titles, setTitles] = useState([]);
    const [filteredTitles, setFilteredTitles] = useState([]);
    const [recommendations, setRecommendations] = useState([]);
    const [error, setError] = useState('');
    const [showSuggestions, setShowSuggestions] = useState(false);

    // Fetch movie titles from backend
    useEffect(() => {
        const loadTitles = async () => {
            try {
                const titles = await fetchMovieTitles();
                // Check if titles is an array
                if (Array.isArray(titles)) {
                    setTitles(titles);
                } else {
                    setError('Unexpected data format.');
                }
            } catch (error) {
                setError('Error fetching movie titles.');
            }
        };

        loadTitles();
    }, []);

    // Filter titles based on input value
    useEffect(() => {
        if (inputValue) {
            setFilteredTitles(titles.filter(title =>
                title.toLowerCase().includes(inputValue.toLowerCase())
            ));
            setShowSuggestions(true);
        } else {
            setFilteredTitles([]);
            setShowSuggestions(false);
        }
    }, [inputValue, titles]);

    // Handle input change
    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    // Handle suggestion click
    const handleSuggestionClick = (title) => {
        setInputValue(title);
        setShowSuggestions(false);
    };

    // Perform search based on the current input value
    const handleSearch = async () => {
        try {
            const data = await fetchRecommendations(inputValue);
            setRecommendations(data);
            setError('');
        } catch (error) {
            setError('Error fetching recommendations.');
        }
    };

    return (
        <div>
            <h1>Movie Recommender</h1>
            <div style={{ position: 'relative' }}>
                <input
                    type="text"
                    value={inputValue}
                    onChange={handleInputChange}
                    placeholder="Type to filter movies"
                />
                {showSuggestions && (
                    <ul style={{ position: 'absolute', top: '100%', left: 0, backgroundColor: 'white', border: '1px solid #ccc', listStyleType: 'none', padding: 0, margin: 0 }}>
                        {filteredTitles.map((title, index) => (
                            <li key={index} onClick={() => handleSuggestionClick(title)} style={{ padding: '8px', cursor: 'pointer' }}>
                                {title}
                            </li>
                        ))}
                    </ul>
                )}
            </div>
            <button onClick={handleSearch}>Search</button>
            {error && <p>{error}</p>}
            <div>
                {recommendations.map((movie) => (
                    <div key={movie.id}>
                        <h2>{movie.title}</h2>
                        <img
                            src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                            alt={movie.title}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MovieSearch;
