// src/api.js

const API_BASE_URL = 'http://localhost:8000'; // Adjust to your backend URL

export const fetchRecommendations = async (movieTitle) => {
    try {
        const response = await fetch(`/movies/recommend/${encodeURIComponent(movieTitle)}/`);
        if (!response.ok) {
            throw new Error(`Network response was not ok: ${response.statusText}`);
        }
        const data = await response.json();
        return data.recommendations;
    } catch (error) {
        console.error('Error fetching movie recommendations:', error);
        throw error;
    }
};

export const fetchMovieTitles = async () => {
    try {
        const response = await fetch('/movies/titles/');
        if (!response.ok) {
            throw new Error(`Network response was not ok: ${response.statusText}`);
        }
        const data = await response.json();
        return data.titles;
    } catch (error) {
        console.error('Error fetching movie titles:', error);
        throw error;
    }
};
