import axios from 'axios';

const API_URL = 'http://localhost:8000/movies/recommend/'; // Update with your Django backend URL

export const fetchRecommendations = async (movieTitle) => {
    try {
        const response = await axios.get(`${API_URL}${movieTitle}/`);
        return response.data.recommendations;
    } catch (error) {
        console.error("Error fetching recommendations:", error);
        throw error;
    }
};

export const fetchMovieTitles = async () => {
    try {
        const response = await axios.get('http://localhost:8000/movies/titles/');
        return response.data.titles;
    } catch (error) {
        console.error("Error fetching movie titles:", error);
        throw error;
    }
};
