import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './MovieCarousel.css'; // Import specific styles for the carousel

const MovieCarousel = ({ movieUrls }) => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        centerMode: true,
        centerPadding: '60px'
    };

    return (
        <div className="movie-carousel">
            <Slider {...settings}>
                {movieUrls.map((url, index) => (
                    <div key={index} className="carousel-item">
                        <img src={url} alt={`Movie ${index}`} />
                    </div>
                ))}
            </Slider>
        </div>
    );
};

export default MovieCarousel;
