import React, { useState, useEffect } from 'react';
import { fetchRecommendations, fetchMovieTitles } from '../api';

const MovieSearch = () => {
    const [movieTitle, setMovieTitle] = useState('');
    const [recommendations, setRecommendations] = useState([]);
    const [titles, setTitles] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const loadTitles = async () => {
            try {
                const titles = await fetchMovieTitles();
                setTitles(titles);
            } catch (error) {
                setError('Error fetching movie titles.');
            }
        };

        loadTitles();
    }, []);

    const handleSearch = async () => {
        try {
            const data = await fetchRecommendations(movieTitle);
            setRecommendations(data);
            setError('');
        } catch (error) {
            setError('Error fetching recommendations.');
        }
    };

    return (
        <div>
            <h1>Movie Recommender</h1>
            <div>
                <select
                    value={movieTitle}
                    onChange={(e) => setMovieTitle(e.target.value)}
                >
                    <option value="">Select a movie</option>
                    {titles.map((title, index) => (
                        <option key={index} value={title}>
                            {title}
                        </option>
                    ))}
                </select>
                <input
                    type="text"
                    value={movieTitle}
                    onChange={(e) => setMovieTitle(e.target.value)}
                    placeholder="Or enter a movie title"
                />
                <button onClick={handleSearch}>Search</button>
            </div>
            {error && <p>{error}</p>}
            <div>
                {recommendations.map((movie) => (
                    <div key={movie.id}>
                        <h2>{movie.title}</h2>
                        <img
                            src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                            alt={movie.title}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MovieSearch;
