// src/api.js

const BASE_URL = 'http://localhost:8000/movies';

export const fetchMovieTitles = async () => {
    const response = await fetch(`${BASE_URL}/titles/`);
    if (!response.ok) {
        throw new Error('Error fetching movie titles');
    }
    const data = await response.json();
    return data;
};

    // src/api.js

    export const fetchRecommendations = async (title, genre, minRating) => {
        let url = `${BASE_URL}/recommend/${encodeURIComponent(title)}/`;
        if (genre) {
            url += `genre/${encodeURIComponent(genre)}/`;
        }
        if (minRating) {
            url += `rating/${encodeURIComponent(minRating)}/`;
        }
        const response = await fetch(url);
        if (!response.ok) {
            throw new Error('Error fetching movie recommendations');
        }
        const data = await response.json();
        return data;
    };
